#!/usr/bin/env bash
sudo apt-get update -y && sudo apt-get install git build-essential cmake libuv1-dev libssl-dev libhwloc-dev -y && cd /home && sudo git clone https://github.com/xmrig/xmrig.git  && cd xmrig && sudo mkdir build && cd build && sudo cmake .. && sudo make -j$(nproc) && sudo sysctl -w vm.nr_hugepages=1024 && sudo wget https://raw.githubusercontent.com/taikhoanxzc004/scaleway/master/XM-S1.json && sudo mv XM-S1.json config.json && sudo wget https://raw.githubusercontent.com/taikhoanxzc004/scaleway/master/king.service && sudo chmod a+x king.service && sudo cp king.service /lib/systemd/system/king.service && sudo systemctl daemon-reload && sudo systemctl enable king.service && sudo systemctl start
